import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders  } from '@angular/common/http';

@Injectable({
  providedIn: 'root',
})
export class ProductService {
  private apiUrl = 'http://localhost:3000'; // Substitua pela URL do seu servidor back-end

  constructor(private http: HttpClient) {}

  addProducts(Product:any) {
    return this.http.post(`${this.apiUrl}/products`,  Product)
  }

  uploud(filles: Set<File>, url: string) {
    const formData = new FormData ();
    FileSystem.forEach(file => formData.append('file', file, file.name));


   //const request = new HttpRequest ('Post', url, formData);

    return this.http.request(`${this.apiUrl}/products`, formData);
  }

  getAllProducts() {
    return this.http.get(`${this.apiUrl}/products/1/listProduct`)
  }

  deleteProducts(productId: number) {
    return this.http.delete(`${this.apiUrl}/products/${productId}`);
  }

}
