import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Injectable({
  providedIn: 'root',
})

export class ProductService {

  private apiUrl = 'http://localhost:3000';

  private apiUrlimage = 'http://localhost:3000/images';

  constructor(private http: HttpClient) {}

  getImageServerUrl() {
    return this.apiUrlimage; 
  }

  getAllProducts() {
    return this.http.get(`${this.apiUrl}/products/1/listProduct`)
  }

  addProducts(formData: FormData) {
    // Configurar o cabeçalho da requisição para "multipart/form-data"
    const headers = new HttpHeaders();
    headers.append('Content-Type', 'multipart/form-data');

    return this.http.post(`${this.apiUrl}/products`, formData, { headers });
  } 

  editProduct(productId: number, productData: any) {
    return this.http.put(`${this.apiUrl}/products/${productId}`, productData);
  }

  deleteProducts(productId: number) {
    return this.http.delete(`${this.apiUrl}/products/${productId}`);
  }

}
