import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-order',
  templateUrl: './order.component.html',
  styleUrls: ['./order.component.css']
})
export class OrderComponent {

  constructor(private router: Router, private route: ActivatedRoute) { }

  ngOnInit() {
    // Verifique se o parâmetro "from" está presente na URL e se é igual a "home".
    const fromParam = this.route.snapshot.queryParamMap.get('from');

    if (fromParam !== 'home') {
      this.router.navigate(['/']); // Redirecionar para a página inicial
    }
  }

  // Método para verificar se o usuário está vindo da página inicial
  private isUserComingFromHomePage(): boolean {
    // Implemente a lógica para verificar se o usuário está vindo da página inicial.
    // Por exemplo, verifique se ele navegou a partir de um link ou botão na página inicial.
    // Se você não tiver essa lógica, você pode definir um parâmetro na URL da página de pedidos
    // quando o usuário acessar a partir da página inicial e verificar a presença desse parâmetro aqui.
    // Exemplo: /pedidos?from=home
    // Nesse caso, você verifica se "from" é igual a "home".
    return true; // Implemente sua própria lógica aqui.
  }

}
