import { Component, OnInit, HostListener, ElementRef } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  logoImagePath = './assets/img/logo.mini.png'; 

  public isNavOpen: boolean;

  constructor() { }

  scroll(id) {
    console.log(`scrolling to ${id}`);
    let el = document.getElementById(id);
    el.scrollIntoView({ behavior: "smooth", block: "start", inline: "nearest" });
    this.isNavOpen = false;
  }

  ngOnInit() {
    this.isNavOpen = false;
  }

  navOpened() {
    console.log("clicado");
    this.isNavOpen = !this.isNavOpen;
  }

  @HostListener('window:scroll', ['$event'])
  onWindowScroll(e) {
    if (window.pageYOffset > 300) {
      let element = document.getElementsByClassName('nav')[0];
      element.classList.add('nav--sticky');
    } else {
      let element = document.getElementsByClassName('nav')[0];
      element.classList.remove('nav--sticky');
    }
  }
}
<nav class="navbar x">
    <div class="container">
        <img [src]="logoImagePath" alt="Logo" class="rounded-circle logo">
        <a class="navbar-brand" href="/">
            Malimpensa Doces
        </a>
        <span class="navbar-toggler-icon"></span>
        <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav ml-auto">
                <!-- Links de navegação aqui -->
                <li class="nav-item">
                    <a class="nav-link" href="/">Home</a>
                </li>
                <!-- Outros links de navegação -->
            </ul>
        </div>
    </div>
</nav>
