import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders  } from '@angular/common/http';

@Injectable({
  providedIn: 'root',
})
export class ProductService {
  private apiUrl = 'http://localhost:3000'; // Substitua pela URL do seu servidor back-end

  constructor(private http: HttpClient) {}

  getAllProducts() {
    return this.http.get(`${this.apiUrl}/products/1/listProduct`)
  }

  deleteProducts(productId: number) {
    return this.http.delete(`${this.apiUrl}/products/${productId}`);
  }

  addProducts(formData: FormData) {
    // Defina o cabeçalho Content-Type como multipart/form-data nas opções da requisição
    const headers = new HttpHeaders();
    headers.append('Content-Type', 'multipart/form-data');

    // Configurar as opções da requisição HTTP, incluindo os cabeçalhos
    const options = { headers };

    // Envia a requisição POST com os dados do formulário e as opções
    return this.http.post(`${this.apiUrl}/products`, formData, options);
  }


}
