import { Component } from '@angular/core';
import { CarrinhoService } from '../services/carrinho.service';

@Component({
  selector: 'app-revisar-pedido',
  templateUrl: './revisar-pedido.component.html',
  styleUrls: ['./revisar-pedido.component.css']
})
export class RevisarPedidoComponent {

  numeroLoja: string = '+5519999372133';
  carrinho: any[] = [];
  produtosDoCarrinho: any[] = []; // Declare a propriedade produtosDoCarrinho aqui

  constructor(
    private carrinhoService: CarrinhoService,
    this.numeroLoja = '+5519999372133', // Substitua '+1234567890' pelo número de telefone da sua loja
    ) {}

  ngOnInit() {
    // Obtenha os produtos do carrinho usando o serviço
    const produtosDoCarrinho = this.carrinhoService.obterProdutosDoCarrinho();

    // Faça algo com a lista de produtos, como exibição em uma tabela
  }

  calcularTotal(): number {
    let total = 0;
    for (const produto of this.produtosDoCarrinho) {
      total += produto.price * produto.quantidade;
    }
    return total;
  }

  concluirCompra() {
    if (this.carrinho.length === 0) {
      return; // Não faça nada se o carrinho estiver vazio
    }

    // Formate os itens do carrinho em uma mensagem
    let mensagem = 'Pedido:';
    for (const item of this.carrinho) {
      mensagem += `%0A${item.quantidade}x ${item.name} - R$${(item.price * item.quantidade).toFixed(2)}`;
    }

    // Crie o link do WhatsApp com a mensagem e o número de telefone da loja
    const linkWhatsApp = `https://wa.me/${this.numeroLoja}?text=${mensagem}`;

    // Abra uma nova aba ou janela do navegador com o link do WhatsApp
    window.open(linkWhatsApp, '_blank');
  }
}
