// src/app/services/product.service.ts

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ApiResponse } from '../shared/models/api-response';// Importe a interface

@Injectable({
  providedIn: 'root',
})
export class ProductService {
  private apiUrl = 'http://localhost:3000'; // Substitua pela URL do seu servidor back-end

  constructor(private http: HttpClient) {}

  getAllProducts(): Observable<ApiResponse> {
    // Adicione <ApiResponse> como tipo genérico para a chamada HTTP
    return this.http.get<ApiResponse>(`${this.apiUrl}/products/1/listProduct`);
  }

  deleteProduct(productId: number): Observable<ApiResponse> {
    // Adicione <ApiResponse> como tipo genérico para a chamada HTTP
    return this.http.delete<ApiResponse>(`${this.apiUrl}/products/${productId}`);
  }

  addProducts(Product: any): Observable<ApiResponse> {
    // Adicione <ApiResponse> como tipo genérico para a chamada HTTP
    return this.http.post<ApiResponse>(`${this.apiUrl}/products`, Product);
  }

  // Adicione outros métodos para interagir com seu servidor conforme necessário
}
