import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class ProductService {
  private apiUrl = 'http://localhost:3000'; // Substitua pela URL do seu servidor back-end

  constructor(private http: HttpClient) {}

  criarProduct(product: any): Observable<any> {
    return this.http.post(`${this.apiUrl}/user-page/1/CreateScheduling`, product);
  }

  comprar(product: any) {
    // Envie os dados do agendamento para o servidor
    return this.http.post(`${this.apiUrl}/user-page/1/CreateScheduling`, product);
  }

  getAllProducts(products: any) {
    return this.http.get(`${this.apiUrl}/Product/1/listProduct`)
  }

  // Adicione outros métodos para interagir com seu servidor conforme necessário
}
